import {
    appEnv
} from './config'

export const AppEnv = {
    Development: 'development',
    SemiProduction: 'semi-production',
    Staging: 'staging',
    Production: 'production'
}

export const isProduction = appEnv === AppEnv.Production
export const isStaging = appEnv === AppEnv.Staging
export const isTest = process.env.NODE_ENV === 'test'
export const isSemiProduction = appEnv === AppEnv.SemiProduction
export const isDevelopment = !isProduction && !isStaging && !isTest && !isSemiProduction